import {useState, useEffect} from 'react'
import { Link, useNavigate } from 'react-router-dom'
import ItemDetailComponent from '../components/ItemDetail/ItemDetailComponent'
import NoFoundComponent from '../components/NoFound/NoFoundComponent'

const dataNoFound = {
  title:'No hay resultado con la búsqueda ingresada',
  description:'Intenta buscar otro usuario'
};

const ItemDetail = ({id}) => {

  const navigate = useNavigate();
  const [data, setData] = useState({})
  const [showData, setShowData] = useState(false);

  const getItemById = async () => {
    const url = `http://localhost:3000/items/${id}`
    const data = await fetch(url)
      .then(res => res.json())
      .then(data => data)
      .catch(err => err)
      console.log(Object.entries(data.data).length)
      if(Object.entries(data.data).length > 0)
        setShowData(true)
      else 
      setShowData(false)
      return data;
  }

  useEffect(() => {
    getItemById().then(data => setData(data.data)).catch(err => console.log(err))
  }, [])


  return (
    <div className='vh-100 row m-0 text-center align-items-center justify-content-center'>   
    {
      showData ?<ItemDetailComponent data={data} />
      :
      <NoFoundComponent  data={dataNoFound} urlReturn={'/'} showLink={false}/>
    }   
      
      <div>
      <button className='btn btn-dark' onClick={() => navigate(-1)}>Regresar</button>
      </div>
    </div>
  )
}

export default ItemDetail
